            .Include(c => c.MsicCodes).ThenInclude(cm => cm.MsicCode)
            .Include(c => c.Departments).ThenInclude(cd => cd.Department)
            .Where(c => !c.IsDeleted);

        return Task.FromResult(query);
    }

