    public async Task<CompanyStaff> GetByStaffId(string staffId)
    {
        var find = await db.CompanyStaffs
            .Include(c => c.CompanyDepartment)
            .Include(c => c.Company)
            .FirstOrDefaultAsync(c => c.StaffId == staffId);
        return find!;
    }


